	sort(a+1,a+cnt+1,cmp);
	for(ri i=1;i<=cnt;i++)
	{
		if(find(a[i].x)==find(a[i].y))
			continue;
		fa[find(a[i].x)]=find(a[i].y);
		ans+=a[i].t;
	}
	cout<<ans<<"\n";
	back 0;
