inline void ade(int&x,int y){x+=y,x>=mod&&(x-=mod);}
inline int add(int x,int y){return x+y>=mod?x+y-mod:x+y;}
void dfs1(int u){
	for(int v:gr[u])dfs1(v);
	for(int i=t+1;i;i--){
		s1[u][i]=s2[u][i]=1,r1[u][i]=r2[u][i]=0;
