#include <iostream>
#include <set>
#include <queue>
#include <vector>
#include <cstdio>

using namespace std;

typedef long long ll;

typedef struct {
	int nxt;
	int end;
} Edge;

int cnt = 0;
int a[400007], pos[800007], nxt[400007], sum[800007], fa[400007], head[400007], depth[400007];
char s[800007];
Edge edge[400007];
priority_queue<int> q;
multiset<int> se;
vector<int> v[400007];

inline void add_edge(int start, int end){
	cnt++;
	edge[cnt].nxt = head[start];
	head[start] = cnt;
	edge[cnt].end = end;
}

void build(int l, int r){
	for (int i = l + 1; i <= r; i = nxt[i] + 1){
		fa[i] = l;
		add_edge(l, i);
		build(i, min(nxt[i], r));
	}
}

void dfs(int u){
	depth[u] = depth[fa[u]] + 1;
	for (int i = head[u]; i != 0; i = edge[i].nxt){
		dfs(edge[i].end);
	}
}

inline ll solve01(int n){
	ll sum = 0, ans = 0;
	for (int i = 1; i < n; i++){
		int size = v[i].size();
		for (int j = 0; j < size; j++){
			sum += a[v[i][j]];
			q.push(a[v[i][j]]);
		}
		sum -= q.top();
		q.pop();
		ans += sum;
	}
	while (q.size() > 1){
		sum -= q.top();
		q.pop();
		ans += sum;
	}
	return ans;
}

inline ll solve10(int n){
	int step = 0, i = 1;
	ll sum = 0;
	for (; i < n; i++){
		int size = v[i].size();
		if (se.empty() && size == 1) continue;
		if (se.size() + size > ++step + 1) break;
		for (int j = 0; j < size; j++){
			sum += a[v[i][j]];
			se.insert(a[v[i][j]]);
		}
	}
	if (se.empty()) return 0;
	int min_val = *se.begin(), max_val = *(--se.end());
	ll ans1 = sum - min_val, ans2 = sum - max_val;
	multiset<int> s1, s2;
	s1.insert(min_val);
	for (int j = i; j < n && !s1.empty(); j++){
		int size = v[j].size();
		multiset<int>::iterator it;
		for (int k = 0; k < size; k++){
			s1.insert(a[v[j][k]]);
		}
		it = --(--s1.end());
		ans1 += *it + (ll)*s1.begin() * (s1.size() - 2);
		s1.erase(it);
	}
	while (s1.size() > 1){
		multiset<int>::iterator it = --(--s1.end());
		ans1 += *it + (ll)*s1.begin() * (s1.size() - 2);
		s1.erase(it);
	}
	s2.insert(max_val);
	for (int j = i; j < n && !s2.empty(); j++){
		int size = v[j].size();
		multiset<int>::iterator it;
		for (int k = 0; k < size; k++){
			s2.insert(a[v[j][k]]);
		}
		it = --(--s2.end());
		ans2 += *it + (ll)*s2.begin() * (s2.size() - 2);
		s2.erase(it);
	}
	while (s2.size() > 1){
		multiset<int>::iterator it = --(--s2.end());
		ans2 += *it + (ll)*s2.begin() * (s2.size() - 2);
		s2.erase(it);
	}
	return min(ans1, ans2);
}

inline ll solve11(int n){
	ll sum = 0, ans = 0;
	for (int i = 1; i < n; i++){
		int size = v[i].size();
		multiset<int>::iterator it;
		for (int j = 0; j < size; j++){
			sum += a[v[i][j]];
			se.insert(a[v[i][j]]);
		}
		ans += sum + (ll)*se.begin() * (se.size() - 2);
		it = --se.end();
		sum -= *it;
		se.erase(it);
	}
	while (se.size() > 1){
		multiset<int>::iterator it = se.begin();
		sum -= *it;
		ans += sum + *it * (se.size() - 1);
		se.erase(--se.end());
		ans += sum;
	}
	return ans;
}

int main(){
	int n, x, y;
	scanf("%d %d %d", &n, &x, &y);
	if (x == 0 && y == 0){
