	fo(i,L,dep) for(int j:g[i]) sum+=v[j];
//	printf("m=%d L=%d pos=%d dep=%d num=%d mn=%d mx=%d\n",m,L,pos,dep,num,mn,mx);
	if(pos==dep){
		cout<<sum-mx;
		exit(0);
	}
	if(pos>=L) cout<<sum+min(play(pos+1,dep,mn),play(pos+1,dep,mx));
	else cout<<sum+play(pos+1,dep,-1);
	exit(0);
}
}

