#include <bits/stdc++.h>
using namespace std;
inline int read()
{
	int x = 0,f = 1ll;
	char ch = getchar();
	while (ch < '0' || ch > '9')
	{
		if (ch == '-') f = -1ll;
		ch = getchar();
	}
	while (ch >= '0' && ch <= '9')
	{
		x = (x << 1ll) + (x << 3) + (ch ^ 48);
		ch = getchar();
	}
	return f * x;
}
inline void write(int x)
{
	if (x < 0) x = -x, putchar('-');
	if (x <= 9) putchar(x+'0');
	if (x > 9) write(x/10),putchar((x % 10) + '0');
}
int n,k,tot,ans,tmp;
int prime[SIZE],minn[SIZE];
bool is_prime[SIZE];
inline void Euler()
{
	for (int i = 2; i < (int)3e7; ++i)
	{
		if (!is_prime[i]) prime[++tot]=i,minn[i]=i;
		for (int j = 1; j <= tot && i * prime[j] < (int)3e7; ++j)
		{
			is_prime[i*prime[j]] = true;
			minn[i*prime[j]] = prime[j];
			if (i % prime[j] == 0) break;
		}
	}
}
inline int solve()
{
	n = read(); k = read(); ans = -2; tmp = 0;
	if (k % 3 != 0) return -1;
	if (n % 3 == 0 && n % 2) return 0;
	if (n % 2 && n % 3)
	{
		n = n / minn[n] * (minn[n] + 1);
		tmp++;
	}
	while (n % 2 == 0)
	{
		ans ++;
		n /= 2;
	}
	return max(0,ans) + tmp;
}
signed main()
{
	Euler();
	for (int T = read(); T; --T) write(solve()),puts("");
	return 0;
}
```

