    }
    for(ll i=1;i<=n;i++)
        for(ll j=1;j<=n;j++)
            if(f[i][j]<1e9) add(i,j,f[i][j]);
    for(ll i=1;i<=n;i++) solve(i);
    scanf("%lld%lld",&k,&q);
    while(q--){
        scanf("%lld%lld",&u,&v);
        if(hashtable[u][v]<1e10){
            printf("%lld\n",hashtable[u][v]);
            continue;
        }
        ll ans=1e18;
        for(ll i=0;i<=k && i<=n-1;i++){
            if(dis[u][v][i]>1e16) continue;
            ans=minn(ans,dis[u][v][i]);
