#include <iostream>
#include <cstring>
using namespace std;
const int N = 80, oo = 0x3f3f3f3f;

struct matrix {
	int n, e[N][N];

	matrix(int a = 0) {
		n = a;
		memset(e, oo, sizeof(e));
		for(int i = 1; i <= n; ++i) e[i][i] = 0;
	}

	matrix operator *(const matrix &a) const {
		matrix ret(n);
		for(int i = 1; i <= n; ++i) {
			for(int j = 1; j <= n; ++j) {
				for(int k = 1; k <= n; ++k)
					ret.e[i][j] = min(ret.e[i][j], e[i][k] + a.e[k][j]);
			}
		}

		return ret;
	}

	matrix operator ^(int b) const {
		matrix a = *this, x(n);
		while(b) {
			if(b & 1) x = x * a;
			a = a * a;
			b >>= 1;
		}

		return x;
	}
}base;

int main() {
	ios::sync_with_stdio(false);
	int n, m, k, q;
	cin >> n >> m;
	base = matrix(n);
	for(int i = 1; i <= m; ++i) {
		int a, b, t;
		cin >> a >> b >> t;
		if(a != b) base.e[a][b] = min(base.e[a][b], t);
	}

	cin >> k >> q; 
	k = min(k, n);
	base = base ^ k;
	for(int i = 1; i <= q; ++i) {
		int c, d;
		cin >> c >> d;
		if(base.e[c][d] != oo) cout << base.e[c][d] << endl;
