		int l=s.size(),l1=0;//l 为字符串长度，l1 为方程右边开始下标 
		for(int i=0;i<l;i++)//看似遍历全部，实则只遍历了左边 
		{
			if(s[i]=='-'&&s[++i]=='>')//分隔，退出循环条件 
			{
				l1=++i;
				break;
			}
			if(s[i]>='0'&&s[i]<='9')//如果读到系数，例如 3H2O 中的 3 
			{
