	}
	int tot=1005;
	rep(i,1,tot){
		f[1][1][i]=1;
	}
	rep(i,2,n){
		memset(f[i&1],0,sizeof(f[i&1]));
		rep(j,1,i){
			rep(k,1,tot-1){
				f[i&1][j][k]=ad(su(mu(f[(i-1)&1][j-1][k],mu(j-1,k)),mu(f[(i-1)&1][j][k],mu(j,ad(k,k)))),mu(f[(i-1)&1][j][k+1],mu(j,k+1)));
			}
		}
		--tot;
		rep(k,1,n)g[k]=0;
		rep(j,1,n){
			rep(k,1,n){
				uad(g[k],f[i&1][j][k]);
			}
		}
		int ans=0;
		rep(k,1,n){
