}
long long n(){
	cin>>x>>y>>z;
	if(x>z){//特判
		cout<<"Merry"<<"\n";
		return 0;
	}
	else{ 
		a=(2*z-1-x)*x/2;//求最多可放入的银色木板
		if(zh(y-a)+x>z){//求剩下的银色木板（如果有）加金色木板是否能装入容器
			cout<<"Merry"<<"\n";
			return 0;
		}
		else{
			cout<<"Renko"<<"\n";
