{
	vis[x] = 1;
	for(int i = 0;i < v[x].size();i++)
	{
		int nw = v[x][i];
		if(!vis[nw]){dfs(nw);siz[x]++;}
	}
	if(x!=1&&(siz[x]&1))b = 0;
}
