值得注意的是每个点要往外找两个点，原因的话考虑这样一个相对位置 $x1<y<x2$。如果先处理 $x2$ 的左边，那 $x1$ 的右边会在 $x2$ 的右边去找，但是如果先处理 $x1$ 的右边，那么 $x2$ 的左边就找不到东西了，这样就会少点。

```cpp
#include<cstdio>
#include<map>
#include<vector>
#include<set>
#include<algorithm>
using namespace std;
typedef long long LL;
const int N=3e5+5;
const int block=70;
const int M=5005;
const LL INF=1ll<<60;
int n,q;
int a[N],b[N];
int to[N],pos[M],m;
LL ans[M][M];
vector<int>in[N];
set<int>s[N];
inline int Read(){
	char ch;
	int f=1;
	while((ch=getchar())<'0'||ch>'9')
		if(ch=='-') f=-1;
	int x=ch^48;
	while((ch=getchar())>='0'&&ch<='9')
		x=(x<<3)+(x<<1)+(ch^48);
	return x*f;
}
inline void print(LL x){
	if(x>=10) print(x/10);
	putchar(x%10+48);
	return ;
}
inline void Print(LL x,char ch='\n'){
	if(x<0){
		putchar('-');
		print(-x);
	}
	else print(x);
	putchar(ch);
	return ;
}
inline LL Max(LL x,LL y){
	return x>y?x:y;
}
inline LL Min(LL x,LL y){
	return x<y?x:y;
}
inline void Cmax(LL&x,LL y){
	if(y>x) x=y;
	return ;
}
inline void Cmin(LL&x,LL y){
	if(y<x) x=y;
	return ;
}
vector<int>pi;
LL mn[N<<1];
inline LL Get(int x,int y){
	int siz=pi.size();
	for(int i=-siz;i<=siz;i++)
		mn[i+N]=INF;
	mn[N]=0;
	int cnt=0;
	LL ss=0,ans=-INF;
	for(int i=0;i<pi.size();i++){
		int ii=pi[i];
		ss+=b[ii];
		if(a[ii]==x) cnt++;
		else cnt--;
		Cmax(ans,ss-mn[cnt+N]);
		Cmin(mn[cnt+N],ss);
	}
	return ans;
}
inline LL Query(int x,int y){
	int sizx=in[x].size(),sizy=in[y].size();
	int i=0,j=0;
	while(i<sizx&&j<sizy){
		int ii=in[x][i],jj=in[y][j];
		if(ii<jj){
			pi.push_back(ii);
			i++;
		}
		else{
			pi.push_back(jj);
			j++;
		}
	}
	while(i<sizx) pi.push_back(in[x][i++]);
	while(j<sizy) pi.push_back(in[y][j++]);
	LL ans=Get(x,y);
	pi.clear();
	return ans;
}
inline void Init(){
	n=Read(),q=Read();
	for(int i=1;i<=n;i++){
		a[i]=Read();
		in[a[i]].push_back(i);
		s[a[i]].insert(i);
	}
	for(int i=1;i<=n;i++)
		b[i]=Read();
	for(int i=1;i<=n;i++)
		if(in[i].size()>block){
			++m;
			pos[m]=i;
			to[i]=m;
		}
	for(int i=1;i<=m;i++)
		for(int j=i+1;j<=m;j++)
			ans[i][j]=ans[j][i]=Query(pos[i],pos[j]);
	return ;
}
inline LL Ask(int x,int y){
	for(int i=0;i<in[x].size();i++){
		int ii=in[x][i];
		pi.push_back(ii);
		for(int j=0;j<=1;j++){
			if(s[y].size()){
				set<int>::iterator it=s[y].lower_bound(ii);
				if(it!=s[y].begin()){
					it--;
					pi.push_back(*it);
					s[y].erase(it);
				}
			}
			if(s[y].size()){
				set<int>::iterator it=s[y].upper_bound(ii);
				if(it!=s[y].end()){
					pi.push_back(*it);
					s[y].erase(it);
				}
			}
		}
	}
	sort(pi.begin(),pi.end());
	LL ans=Get(x,y);
	while(pi.size()){
		int ii=pi.back();
		pi.pop_back();
		if(a[ii]==y) s[y].insert(ii);
	}
	return ans;
}
inline void Swap(int&x,int&y){
	int tmp=x;
	x=y;y=tmp;
	return ;
}
inline void Solve(){
	for(;q;q--){
		int x=Read();
		int y=Read();
		if(in[x].size()>in[y].size()) Swap(x,y);
		if(in[x].size()>block) Print(ans[to[x]][to[y]]);
		else if(in[y].size()<=block) Print(Query(x,y));
		else Print(Ask(x,y));
	}
	return ;
}
int main(){
	Init();
	Solve();
	return 0;
}
