		f[i] = i;
		siz[i].push_back(i);
	}
	for(int i = 1; i <= m; i ++){
		cin >> u >> v;
		if(u > v) swap(u, v);
		vv.push_back(make_pair(u, v));
	}
	string opt;
	for(int i = 1; i <= Q; i ++){
