		if(opt == "GC") t.push_back(i);
		else {
			int qwq;
			cin >> qwq;
			u = vv[qwq - 1].first; 
			v = vv[qwq - 1].second; 

			if(u > v) swap(u, v);
			q.push_back(make_pair(u, v));
			qt.push_back(i);
			m1[make_pair(u, v)] = 1;
		}
	}
	for(int i = 0; i <= m - 1; i ++){
		u = vv[i].first; v = vv[i].second;
		if(m1.find(make_pair(u, v)) == m1.end()){
			hb(u, v);
		}
	}
	for(int i = 1; i <= n; i ++){
		tim[i] = make_pair(0, i);
	}
	for(int i = 1; i <= n; i ++){
