		if(uu == _v) continue;
		if(uu == find(1)){
			for(int j = 0; j < siz[_v].size(); j ++){
				tim[siz[_v][j]] = make_pair(qt[i], siz[_v][j]);
			}
			hb(u, v);
		}else if(_v == find(1)){
			for(int j = 0; j < siz[uu].size(); j ++){
				tim[siz[uu][j]] = make_pair(qt[i], siz[uu][j]);
			}
			hb(u, v);
		}else hb(u, v);
		//cout << i << endl;
	}
