	for(int i=1;i<=n;++i) tin>>a[i];
	for(int i=1;i<=n;++i) tin>>c[i],id[i]=(i-1)/len+1;
	for(int i=1;i<=id[n];++i) L[i]=(i-1)*len+1,R[i]=i*len; R[id[n]]=n;
	for(int i=1;i<=id[n];++i) build(i);
