						a[i]-=t[rev[id[l]][y]].tag;
						++t[rev[id[l]][y]].size;
					}
				}
				continue;	
			}	
			for(int i=l;i<=R[id[l]];++i){
				res=find(poi[i]);
				if(t[res.fi].col!=x)continue;
				--t[res.fi].size;
				if(!t[res.fi].size)
					rev[id[l]][t[res.fi].col]=0;
				a[i]+=res.se+t[res.fi].tag;
				if(!rev[id[l]][y]){
					rev[id[l]][y]=++cnt;
					poi[i]=cnt;
