类似 [P8576](https://www.luogu.com.cn/blog/St-John/p8576-dtoi-2-xing-zhi-jie) 的一道题，但是更难。  
这道题需要维护操作一，必然是分块。  
接下来的操作二则是需要思考的了。  
操作三平凡。  

操作一可以用并查集来维护。  
但是操作二是需要打标记的。  
在操作一时，标记是无法维护的。  
所以考虑转移标记，对于每次找父亲，都将标记下传。  
所以在操作一时，要新建一个节点，使得它的信息包含 $x,y$ ，党派是 $y$ ，这样不会有标记重复的情况。  

```cpp
#include<cstdio>
#define _for(i,a,b) for(register int i=(a);i<=(b);++i)
#define __for(i,a,b) for(register int i=(a);i>=(b);--i)
#define Re register int
#define il inline
#define pc putchar
using namespace std;
typedef long long ll;
const int N=2.5e5+10,M=N*10+10,inf=2147483647;
il int re(){
	int x=0;
	bool f=0;
	char ch=getchar();
	while(ch<'0'||ch>'9')
		f|=ch=='-',ch=getchar();
	while(ch>='0'&&ch<='9')
		x=(x<<1)+(x<<3)+(ch^48),ch=getchar();
	return f?-x:x;
}
void pr(ll x){
	if(x<0) x=-x,pc('-');
	if(x>9) pr(x/10);
	pc(x%10|48);
}

const int B=500,L=B+10;
int n,m;
int fa[M];
ll add[M];
int find(int x){
//查询时推标记，根节点会被用很多次，所以不推根节点的标记
//因此根节点一定是新点
	if(x==fa[x]) return x;
	int f=find(fa[x]);
	if(f!=fa[x]) add[x]+=add[fa[x]];
	return fa[x]=f;
}
int pos[N],bl[L],br[L],cnt;
ll a[N],sum[L];int b[N];
struct node{
	int head,num;
}e[L][N];

il void init(int x){
	_for(i,bl[x],br[x]){
		sum[x]+=a[i];
		if(!e[x][b[i]].head){
			e[x][b[i]].head=++cnt;
			fa[cnt]=cnt;
		}
		fa[i]=e[x][b[i]].head;
		++e[x][b[i]].num;
	}
}

il void calc1(int now,int l,int r,int x,int y){
	if(!e[now][x].head) return ;
	++cnt;fa[cnt]=cnt;
	if(e[now][y].head)
		fa[e[now][y].head]=cnt;
	e[now][y].head=cnt;
	_for(i,l,r){
		int f=find(i);
		a[i]+=add[i];
		add[i]=0;
		if(f==e[now][x].head){
			a[i]+=add[f];
			fa[i]=cnt;
			++e[now][y].num;
			--e[now][x].num;
		}
	}
}
il void calc2(int now,int x,int y){
	if(!e[now][x].head) return ;
	if(!e[now][y].head){
		e[now][y].head=e[now][x].head;
		e[now][y].num=e[now][x].num;
		e[now][x].head=e[now][x].num=0;
	}
	else{
		++cnt;
		fa[cnt]=cnt;
		fa[e[now][x].head]=cnt;
		fa[e[now][y].head]=cnt;
		e[now][y].num+=e[now][x].num;
		e[now][x].head=e[now][x].num=0;
		e[now][y].head=cnt;
	}
}
il void calc3(int now,int l,int r,int x,ll v){
	if(!e[now][x].head) return ;
	_for(i,l,r){
		int f=find(i);
		a[i]+=add[i];add[i]=0;
		if(f==e[now][x].head){
			a[i]+=v;
			sum[now]+=v;
		}
	}
}
il void calc4(int now,int x,ll v){
	if(!e[now][x].head) return ;
	add[e[now][x].head]+=v;
	sum[now]+=v*e[now][x].num;
}
il ll calc5(int l,int r){
	ll s=0;
	_for(i,l,r){
		int f=find(i);
		a[i]+=add[i];
		add[i]=0;
		s+=a[i]+add[f];
	}
	return s;
}

signed main(){
	n=cnt=re();
	int q=re();
	m=re();
	_for(i,1,n){
		a[i]=re();
		pos[i]=i/B+1;
		if(pos[i]!=pos[i-1]){
			bl[pos[i]]=i;
			br[pos[i-1]]=i-1;
		}
	}
	br[pos[n]]=n;
	_for(i,1,n) b[i]=re();
	_for(i,1,pos[n]) init(i);

	while(q--){
		int op=re();
		if(op==1){
			int l=re(),r=re(),x=re(),y=re();
			if(x==y) continue;
			if(pos[l]==pos[r]){
				calc1(pos[l],l,r,x,y);
			}
			else{
				int L,R;
				if(l!=bl[pos[l]]){
					calc1(pos[l],l,br[pos[l]],x,y);
					L=pos[l]+1;
				}
				else L=pos[l];
				if(r!=br[pos[r]]){
					calc1(pos[r],bl[pos[r]],r,x,y);
					R=pos[r]-1;
				}
				else R=pos[r];
				_for(i,L,R)
					calc2(i,x,y);
			}
		}
		else if(op==2){
			int l=re(),r=re(),x=re();ll v=re();
			if(pos[l]==pos[r]){
				calc3(pos[l],l,r,x,v);
			}
			else{
				int L,R;
				if(l!=bl[pos[l]]){
					calc3(pos[l],l,br[pos[l]],x,v);
					L=pos[l]+1;
				}
				else L=pos[l];
				if(r!=br[pos[r]]){
					calc3(pos[r],bl[pos[r]],r,x,v);
					R=pos[r]-1;
				}
				else R=pos[r];
				_for(i,L,R)
					calc4(i,x,v);
			}
		}
		else{
			int l=re(),r=re();
			ll ans=0;
			if(pos[l]==pos[r]){
				ans=calc5(l,r);
			}
			else{
				int L,R;
				if(l!=bl[pos[l]]){
					ans+=calc5(l,br[pos[l]]);
					L=pos[l]+1;
				}
				else L=pos[l];
				if(r!=br[pos[r]]){
					ans+=calc5(bl[pos[r]],r);
					R=pos[r]-1;
				}
				else R=pos[r];
				_for(i,L,R)
					ans+=sum[i];
			}
			pr(ans),pc('\n');
		}
	}
	return 0;
}
