 				c1=(c1-1ll*C(qz[i-1]+i-1,i-1)*C(sum-qz[i-1]+n-i-1,n-i)%mod+mod)%mod;
				c2=(c2-1ll*C(qz[i-1]+i-1,i)*C(sum-qz[i-1]+n-i-1,n-i)%mod+mod)%mod;
