using namespace std;

ull T, N;

signed main()
{
    cin >> T;
    for(ull i = 1; i <= T; ++ i)
    {
        cin >> N;
        bool is_all_nine = true;
        ull cnt = 0, tmp[25];
        memset(tmp, 0, sizeof(tmp));
        ull tmpN = N;
        if(N <= 8)
        {
            cout << N << endl;
            continue;
        }
        
        if(N % 10 == 0)
            -- N, -- tmpN;

        while(N)
        {
            tmp[++ cnt] = N % 10;
            N /= 10;
            if(tmp[cnt] != 9)
                is_all_nine = false;
        }

        if(is_all_nine)
            cout << tmpN + 1 << endl;
        else
        {
            if(tmp[cnt] == 9)
            {
                ull T, pos = 0;
                for(ull i = cnt; i >= 1; -- i)
                {
                    if(tmp[i] != 9)
                    {
                        T = tmp[i];
                        pos = i;
                        break;
                    }
                }

                if(pos == 1)
                {
                    printf("%d", T + 1);
                    for(ull i = 1; i <= cnt - 2; ++ i)
                        putchar('9');

                    puts("8");
                    continue;
                }

                putchar('1');
                for(ull i = 1; i <= pos - 2; ++ i)
                    putchar('0');

                putchar('0' + T + 1);
                for(ull i = 1; i <= cnt - pos - 1; ++ i)
                    putchar('9');

                puts("8");
            }
            else
            {
                cout << 1;
                for(ull i = 1; i <= cnt - 2; ++ i)
                    cout << 0;
                
                cout << tmp[cnt] << endl;
            }
        }
    }
    
    return 0;
}
