	scanf("%d", &t);
	while (t--) {
		memset(b, 0, sizeof b);
		f = 1, ans = p = 0;
		scanf("%lld", &n), m = n;
		while (n) a[++p] = n % 10, n /= 10;
		b[p] = 1;
		for (int i = p; i; i--) {
			if (a[i] == 9) b[p - i + 1] = 9;
			else { f = 0, b[p - i + 1] = a[i] + 1; break;}
		}
		if (f) { printf("%lld\n", m + 1); continue; }
		for (int i = p; i; i--) ans = ans * 10 + b[i];
		if (a[1] != 9) ans = min(ans, rev(m + 1));
		printf("%lld\n", ans - 1);
	}
}
