			}
			++c9;
		}
		for(int i=len;i>=1;--i){
			if(s[i]!='0'){
				break;
			}
			++c0;
		}
		if(c9==len){
			pc('1');
			for(int i=1;i<=len;++i) pc('0');
			puts("");
		}
		else if(c9==0){
			pc('1');
			for(int i=2;i<len;++i){
				pc('0');
			}
			if(c0==len-1) pc(s[1]-1);
			else pc(s[1]);
			puts("");
		}
		else if(c9==len-1){
			pc(s[len]+1);
			for(int i=2;i<len;++i) pc('9');
			pc('8');
			puts("");
		}
		else{
			pc('1');
			for(int i=2;i<=len-c9-1;++i) pc('0');
			if(c0+c9+1>=len) pc(s[c9+1]);
			else pc(s[c9+1]+1);
			for(int i=len-c9+1;i<len;++i) pc('9');
			pc('8');
			puts("");
		}
	}
	return 0;
}
