	}
	cout<<"rl";
	for(int i=3;i<=m;i++){
		if(i%2==1)cout<<"l";
		else cout<<"r";
	}
	for(int i=1;i<=n-m;i++)
		if(i%3==1||i%3==2)cout<<"l";
