	while(a[k--]>w);//寻找所能买的最大价值的东西
	k++;
	int p=a[k],ans=0;//p 表示每次换之后当前剩余的钱数
	for(int i=1;(i==1||i<k)&&p>=a[i];i++)
	{
		p-=a[i];//扣除 a[i] 的钱数
		ans++;
		//cerr<<"DEBUG:"<<p<<endl;
	}
	cout<<ans;
