int main()
{
	scanf("%lld",&n);
	for(i=1;i<=n;i++) scanf("%lld",&a[i]);
	sort(a+1,a+n+1);
	scanf("%lld",&m);
	for(i=n;i>0;i--)
		if(a[i]<=m){ k=i;break; }//a[k]就是最初选的那个数
	if(!k)
	{puts("0");return 0;
	}//特判一个数都选不到
	for(i=1;i<k;i++)
	{
		s+=a[i];
		if(s>a[k])break;
	}
	printf("%lld",(i==1 ? i:i-1));//特判一个数都换不到
	return 0;
}
