    T = read();
    while (T--) {
        memset(ok, 0, sizeof(ok));
        memset(ch, 0, sizeof(ch));
        memset(ncnt, 0, sizeof(ncnt));
        memset(nums, 0, sizeof(nums));
        for (int i = 0; i <= 100000; i++) l[i].clear();

        n = read(), x = read(), y = read(); m = 0;
        for (int i = 1; i <= n; i++) {
            b[i] = read();
            if (l[b[i]].size() == 0) nums[++m] = b[i];
            l[b[i]].push_back(i);
            ncnt[b[i]]++;
        }
        sort(nums + 1, nums + m + 1, cmp);
        for (int i = 1; i <= m; i++) {
            int nb = nums[i];
            int s = l[nb].size() - 1;
            if ((s % 2 == 0) && x >= s) {
                ch[nb] = s;
                x -= ch[nb];
                ok[nb] = 1;
            } else if (x >= s && y > 0) {
                ch[nb] = s - 1;
                x -= ch[nb];
                y--;
                ok[nb] = 1;
            } else if (x >= s) {
                ch[nb] = s;
                x -= ch[nb];
            } else {
                ch[nb] = x;
                y -= (s - x);
                x = 0;
                ok[nb] = 1;
            }
        }
        bool st = 0;
