	cin>>n>>Generator::k1>>Generator::k2>>Generator::thres;
    Generator::generate();
	mi[n+1]=2000000000;
	for(int i=n;i>=1;i--)
		if(a[i]<mi[i+1])mi[i]=a[i],mit[i]=i;
		else mi[i]=mi[i+1],mit[i]=mit[i+1];
	for(int i=1;i<=n;)
		if(mi[i]<a[i])swap(a[i],a[mit[i]]),i=mit[i]+1;
