        if(a[i] < a[nxt[i + 1]]) nxt[i] = i;
        else nxt[i] = nxt[i + 1];
    }
    for(int i = 1; i < n; i++){
        if(a[nxt[i + 1]] < a[i]){
            swap(a[i], a[nxt[i + 1]]);
