代码：
```cpp
#include <iostream>
#include <algorithm>
#include <set>
#include <map>
#include <vector>
#include <cstdio>

using namespace std;

typedef unsigned long long ull;

typedef struct {
	int nxt;
	int end;
} Edge;

const int N = 1299709 + 7, M = 1e5 + 7;
int prime[N];
bool p[N];

typedef struct {
	int cnt;
	int head[M], size[M];
	ull dp[M];
	Edge edge[M];

	inline void init(int n){
		cnt = 0;
		for (register int i = 1; i <= n; i++){
			head[i] = 0;
		}
	}

	inline void add_edge(int start, int end){
		cnt++;
		edge[cnt].nxt = head[start];
		head[start] = cnt;
		edge[cnt].end = end;
	}

	void dfs1(int u){
		size[u] = 1;
		for (register int i = head[u]; i != 0; i = edge[i].nxt){
			int x = edge[i].end;
			dfs1(x);
			size[u] += size[x];
		}
	}

	void dfs2(int u){
		dp[u] = 1;
		for (register int i = head[u]; i != 0; i = edge[i].nxt){
			int x = edge[i].end;
			dfs2(x);
			dp[u] += dp[x] * prime[size[x]];
		}
	}
} Hash;

typedef struct Node_tag {
	int pos;
	int size;
	ull hash;
	Node_tag(int pos_, int size_, ull hash_){
		pos = pos_;
		size = size_;
		hash = hash_;
	}
} Node;

Hash g, h;
map<int, bool> mp[100007];

bool operator <(const Node a, const Node b){
	if (a.size != b.size) return a.size < b.size;
	return a.hash < b.hash;
}

inline void init1(){
	int cnt = 0;
	p[0] = p[1] = true;
	for (register int i = 2; i < N; i++){
		if (!p[i]) prime[++cnt] = i;
		for (register int j = 1; j <= cnt && i * prime[j] < N; j++){
			p[i * prime[j]] = true;
			if (i % prime[j] == 0) break;
		}
	}
}

inline void init2(int n){
	for (register int i = 1; i <= n; i++){
		mp[i].clear();
	}
}

inline int read(){
	int sign = 1, ans = 0;
	char ch = getchar();
	while (ch < '0' || ch > '9'){
		if (ch == '-') sign = -sign;
		ch = getchar();
	}
	while (ch >= '0' && ch <= '9'){
		ans = ans * 10 + (ch ^ 48);
		ch = getchar();
	}
	return sign * ans;
}

bool dfs(int u, int v){
	int k = g.size[u] - h.size[v];
	if (k < 0) return false;
	if (k == 0) return g.dp[u] == h.dp[v];
	if (mp[u].count(v)) return mp[u][v];
	int size1, size2;
	multiset<Node> s;
	vector<Node> vec1, vec2;
	for (register int i = g.head[u]; i != 0; i = g.edge[i].nxt){
		int x = g.edge[i].end;
		s.insert(Node(x, g.size[x], g.dp[x]));
	}
	for (register int i = h.head[v]; i != 0; i = h.edge[i].nxt){
		int x = h.edge[i].end;
		Node y(x, h.size[x], h.dp[x]);
		if (s.count(y)){
			s.erase(s.find(y));
		} else {
			vec1.push_back(y);
		}
	}
	size1 = vec1.size();
	size2 = s.size();
	if (size2 < size1 || size2 > k) return mp[u][v] = false;
	int diff = size2 - size1;
	for (register int i = 1; i <= diff; i++){
		vec1.push_back(Node(0, 0, 0));
	}
	sort(vec1.begin(), vec1.end());
	while (!s.empty()){
		multiset<Node>::iterator cur = s.begin();
		vec2.push_back(*cur);
		s.erase(cur);
	}
	do {
		bool flag = true;
		for (register int i = 0; i < size2; i++){
			if (vec1[i].pos != 0 && !dfs(vec2[i].pos, vec1[i].pos)){
				flag = false;
				break;
			}
		}
		if (flag) return mp[u][v] = true;
	} while (next_permutation(vec1.begin(), vec1.end()));
	return mp[u][v] = false;
}

int main(){
	int c = read(), t = read(), k = read();
	init1();
	for (register int i = 1; i <= t; i++){
		int n1 = read(), root1, n2, root2;
		init2(n1);
		g.init(n1);
		for (register int j = 1; j <= n1; j++){
			int a = read();
			if (a == -1){
				root1 = j;
			} else {
				g.add_edge(a, j);
			}
		}
		g.dfs1(root1);
		g.dfs2(root1);
		n2 = read();
		h.init(n2);
		for (register int j = 1; j <= n2; j++){
			int b = read();
			if (b == -1){
				root2 = j;
			} else {
				h.add_edge(b, j);
			}
		}
		h.dfs1(root2);
		h.dfs2(root2);
		if (dfs(root1, root2)){
			cout << "Yes" << endl;
		} else {
			cout << "No" << endl;
		}
	}
	return 0;
}
