#include<cstdio>
#include<iostream>//杜绝万能头，从我做起(雾
using namespace std;
int n,mp[101][101];
char ch;
int ans;//记录答案
void pd(int i,int j,int k)
{

	if(n-i < k-j)
		return;
		//cout<<i<<endl; 
	int x1 = i;
	int y1 = j;
	int y2 = k;
	for(int u = 1 ; u< k-j+1 ; u++)
	{
		x1++;
		y1++;
		i++;
		y2--;
		if(mp[x1][y1] == 0)return;
		if(mp[i][y2] == 0)return;
	}//遍历“X”形，判断是否全为1，否则return
	ans++;
}
int main()
{
	scanf("%d",&n);
	for(int i = 1 ; i<= n ; i++ )
	{
		for(int j = 1 ; j<= n ; j++)
		{
			cin>>ch;
			mp[i][j] = ch-'0';
		}	
	}
	//读入
	for(int i = 1 ; i <= n ; i++)
	{
		for(int j = 1 ; j <= n ; j++)
		{
			if(mp[i][j] == 1)
			{
				for(int k = j+1;  k <= n ; k++)
				{
					if(mp[i][k] == 1)
					{
						pd(i,j,k);
					}
				}	
			}
		}
	}//遍历并判断，记录答案
	printf("%d",ans);//输出
	return 0;//轻松结束，撒花！
} 
```

