template<typename T>
T &read(T &r){
	r=0;bool w=0;char ch=getchar();
	while(ch<'0'||ch>'9')w=ch=='-'?1:0,ch=getchar();
	while(ch>='0'&&ch<='9')r=r*10+(ch^48),ch=getchar();
	return r=w?-r:r;
}
template<typename T1,typename... T2>
void read(T1 &x,T2& ...y){read(x);read(y...);}
const int mod=1000033;
inline void cadd(int &x,int y){x=(x+y>=mod)?(x+y-mod):(x+y);}
inline void cdel(int &x,int y){x=(x-y<0)?(x-y+mod):(x-y);}
inline int add(int x,int y){return (x+y>=mod)?(x+y-mod):(x+y);}
inline int del(int x,int y){return (x-y<0)?(x-y+mod):(x-y);}
int qpow(int x,int y){
	int s=1;
	while(y){
		if(y&1)s=1ll*s*x%mod;
		x=1ll*x*x%mod;
		y>>=1;
	}
	return s;
}
mt19937 rnd(time(NULL)^(ull)(new char));
int bit(int x){
	return 1<<(x-1);
}
const int N=19;
int n,T;
int a[N],b[N],c[N],inv1000=qpow(1000,mod-2);
int nxt[N];
int f[(1<<18)+10][N][2];
vi vec[N];
int m,u[N],v[N],w[N],ans[N+1];
int iv[N];
void Gauss(){
	for(int i=1;i<=m;i++){
		iv[i]=qpow(v[i],mod-2);
	}
	int x=del(0,v[m]),y=w[m];
	for(int i=m-1;i>=2;i--){
		x=1ll*x*v[i]%mod;
		y=1ll*y*v[i]%mod;
		x=del(0,x);
		y=del(w[i],y);
	}
	ans[1]=1ll*del(w[1],1ll*v[1]*y%mod)*qpow(add(u[1],1ll*v[1]*x%mod),mod-2)%mod;
	ans[m+1]=ans[1];
	for(int i=m;i>=2;i--){
		ans[i]=del(w[i],1ll*ans[i+1]*v[i]%mod);
	}
}
void solve(int S){
	int lst=0,fir=0;
	vi p;
	for(int i=n;i>=1;i--){
		nxt[i]=0;
		if(bit(i)&S){
			p.pb(i);
			nxt[i]=lst;
			lst=i;
			if(!fir)fir=i;
		}
	}
	nxt[fir]=lst;
	reverse(p.begin(),p.end());
	m=p.size();
	for(int i=0;i<m;i++){
		int x=p[i];
		u[i+1]=1;
		v[i+1]=del(0,c[x]);
		w[i+1]=0;
		cadd(w[i+1],1ll*a[x]*f[S^bit(x)][nxt[x]][0]%mod);
		cadd(w[i+1],1ll*b[x]*f[S^bit(x)][nxt[x]][0]%mod);
		cadd(w[i+1],1);
	}
	Gauss();
	for(int i=0;i<m;i++)
		f[S][p[i]][1]=ans[i+1];
	//-----------------------------------
	for(int i=0;i<m;i++){
		int x=p[i];
		u[i+1]=1;
		v[i+1]=del(0,b[x]);
		w[i+1]=0;
		cadd(w[i+1],1ll*a[x]*f[S^bit(x)][nxt[x]][0]%mod);
		cadd(w[i+1],1ll*c[x]*f[S][nxt[x]][1]%mod);
		cadd(w[i+1],1);
	}
	Gauss();
	for(int i=0;i<m;i++)
		f[S][p[i]][0]=ans[i+1];
}
