    {
        int g=gcd(a,b);
        a/=g;
        b/=g;
    }
    void operator += (fraction &g)
    {
        int l=lcm(b,g.b);
        int bx=l/b,gbx=l/g.b;
        a*=bx,g.a*=gbx;
        b=l,g.b=l;
        a+=g.a;
