	int n;
	cin>>n;
	if(n<5)
		puts("-1"),exit(0);
	if(n==5)
		cout<<"01100"<<endl,exit(0);
	if((n-5)%4==0)
	{
		cout<<"01100";
		for(int i=1;i<=(n-5)/4;i++)
			cout<<"0110";
