	for (int i = 1; i <= n; i++) 
	{
		a[i] = read();
		sum[i] = sum[i - 1] + a[i];
		sm[i] = (sm[i - 1] + a[i] * a[i] % mod) % mod;
	}
	int pos = n, ans = 0;
	for (int i = 1; i <= k; i++)
	{
		while (pos && a[pos] + i > -(a[pos] + 1)) pos--;
		ans = ((ans + sm[pos] + 2 * sum[pos] % mod) % mod + pos) % mod;
		ans = (ans + (sm[n] - sm[pos] + 2 * mod) % mod + 2 * i * ((sum[n] - sum[pos]) % mod) % mod) % mod;
		ans = (ans + (n - pos) * i % mod * i % mod) % mod;
	}
