    }
    tot = 0;
    dfs(1, 0);
    return 0;
}
```

判环：并查集。对于每条边，它的两点若不在一个集合，就加入到图中，否则记录这两个点，作为环的起点和终点。  
寻找路径：使用 dfs，为了易于输出路径，使用 vis 数组。不算环的话，图就是一个树，两点之间路径是唯一的，找到了直接输出路径（路径就是所有 vis 为 true 的点）即可。
```cpp
#include<bits/stdc++.h>
#define maxn 100005
using namespace std;
int n,hs,he;//hs指环的起点，he指环的终点 
int fa[maxn];
bool vis[maxn];
vector<int>g[maxn];
void add(int u,int v){
	g[u].push_back(v);
	g[v].push_back(u);
}
int find(int x){
	if(fa[x]==x)return x;
	return fa[x]=find(fa[x]);
}
void unionn(int x,int y){
	int xx=find(x);
	int yy=find(y);
	fa[yy]=xx;
}
//find和unionn都是并查集操作 
int dfs(int u){
	if(u==he){//如果找到了路径 
		for(int i=1;i<=n;i++)
			if(vis[i])
				cout<<i<<" ";
		return 0;
	}
	for(int i=0;i<g[u].size();i++){
		int v=g[u][i];
		if(!vis[v]){
			vis[v]=1;
			dfs(v);
			vis[v]=0;//回溯 
		}
	}
}	
int main(){
	cin>>n;
	for(int i=1;i<=n;i++)fa[i]=i;
	for(int i=1;i<=n;i++){
		int a,b;
		cin>>a>>b;
		int s=find(a);
		int e=find(b);
		if(s==e){hs=a;he=b;}//找环 
		else{
			add(a,b);
			unionn(a,b);
		}
	}
	vis[hs]=1;
	dfs(hs);//开始搜索 
	return 0;
}
```
