void init()
{
    m["Jan"] = 1;
    m["Feb"] = 2;
    m["Mar"] = 3;
    m["Apr"] = 4;
    m["May"] = 5;
    m["Jun"] = 6;
    m["Jul"] = 7;
    m["Aug"] = 8;
    m["Sep"] = 9;
    m["Oct"] = 10;
    m["Nov"] = 11;
    m["Dec"] = 12;
}

int main()
{
    init();

	string date;
    cin >> date;
    month = date.substr(0, 3);
	day = date.substr(3, 2);
	
	
    istringstream is(date.substr(3, 2));
    is >> day;
	string month = date.substr(0, 3);
	
    cout << m[month] << ' ' << day << endl;
    return 0;
}
```
