const ll N=1e6;
ll seg[N],rev[N],top[N],p[N],tot=1;
ll son[N],size[N],fa[N],dep[N];
vector<ll> a[N];
void dfs1(ll u,ll f){
	fa[u]=f;
	dep[u]=dep[f]+1;
	size[u]=1;
	for(int i=0;i<a[u].size();i++){
		ll v=a[u][i];
		if(v==f) continue;
		dfs1(v,u);
		size[u]+=size[v];
		if(size[v]>size[son[u]]) son[u]=v; 
	}
}
void dfs2(ll u,ll f){
	if(son[u]){
		seg[son[u]]=++tot;
		rev[tot]=son[u];
		top[son[u]]=top[u];
		dfs2(son[u],u);
	}
	for(int i=0;i<a[u].size();i++){
		ll v=a[u][i];
		if(!top[v]&&v!=f){
			top[v]=v;
			seg[v]=++tot;
			rev[tot]=v;
			dfs2(v,u);
		}
	} 
}
//以上部分为树链剖分初始化
ll n,m;
ll sum[N];
ll ask(ll x,ll y){
	ll fx=top[x];
	ll fy=top[y];
	ll ans=0;
	while(fx!=fy){
		if(dep[fx]<dep[fy]) swap(fx,fy),swap(x,y);
		ans+=(sum[seg[x]]-sum[seg[fx]-1]);
		x=fa[fx],fx=top[x]; 
	}
	if(dep[x]>dep[y]) swap(x,y);
	ans+=(sum[seg[y]]-sum[seg[x]-1]);
	return ans;
}
