struct Pair{
	ll x,y;
	Pair operator *(const ll&tmp )const{
		Pair c;
		c.x=x*tmp%mod1,c.y=y*tmp%mod2;
		return c;
	}
	Pair operator +(const Pair&tmp )const{
		Pair c;
		c.x=(x+tmp.x)%mod1,c.y=(y+tmp.y)%mod2;
		return c;
	}
	Pair operator -(const Pair&tmp )const{
		Pair c;
		c.x=(x-tmp.x+mod1)%mod1,c.y=(y-tmp.y+mod2)%mod2;
		return c;
	}
	void clear(){x=0,y=0;}
}p[N],sam,cur,pas[N],ou[N];

int main(){
