#include <stdio.h>
#include <stdlib.h>
#include <time.h>

typedef long long ll;

int val[500007];
ll full[500007], cur[500007];

inline int rand32(){
	return rand() * rand();
}

inline int read(){
	int ans = 0;
	char ch = getchar();
	while (ch < '0' || ch > '9'){
		ch = getchar();
	}
	while (ch >= '0' && ch <= '9'){
		ans = ans * 10 + (ch ^ 48);
		ch = getchar();
	}
	return ans;
}

int main(){
	int n = read(), m = read(), q;
	ll goal = 0, sum = 0;
	srand(time(NULL));
	for (register int i = 1; i <= n; i++){
		val[i] = rand32();
		goal += val[i];
	}
	for (register int i = 1; i <= m; i++){
		int u = read(), v = read();
		full[v] += val[u];
	}
	for (register int i = 1; i <= n; i++){
		cur[i] = full[i];
		sum += full[i];
	}
	q = read();
	for (register int i = 1; i <= q; i++){
		int t = read(), u = read();
		if (t % 2 == 1){
			int v = read();
			if (t == 1){
				cur[v] -= val[u];
				sum -= val[u];
			} else {
				cur[v] += val[u];
				sum += val[u];
			}
		} else if (t == 2){
			sum -= cur[u];
			cur[u] = 0;
		} else {
			sum += full[u] - cur[u];
			cur[u] = full[u];
		}
		if (sum == goal){
			printf("YES\n");
		} else {
			printf("NO\n");
		}
	}
	return 0;
}
