    void link(int u,int v,T w=0){add(u,v,w),add(v,u,w);}
};
int n,m,q,tot;
mt19937_64 rng(time(0));
ULL w[500010],sum,h[500010],inn[500010],tar[500010],ind[500010];
int main(){
