	for(int i=1,u,v;i<=m;i++) scanf("%d%d",&u,&v),sum^=w[u],h[v]^=w[u],inn[v]++;
	memcpy(tar,h,sizeof tar);
	memcpy(ind,inn,sizeof ind);
	for(int i=1;i<=n;i++) tar[0]^=w[i];
	scanf("%d",&q);
