		scanf("%d%d",&op,&u);
		if(op==1) scanf("%d",&v),h[v]^=w[u],sum^=w[u],inn[v]--,tot--;
		else if(op==3) scanf("%d",&v),h[v]^=w[u],sum^=w[u],inn[v]++,tot++;
		else if(op==2){
			tot-=inn[u],sum^=h[u];
			inn[u]=h[u]=0;
			tot+=inn[u],sum^=h[u];
		}else if(op==4){
			tot-=inn[u],sum^=h[u];
			inn[u]=ind[u],h[u]=tar[u];
			tot+=inn[u],sum^=h[u];
		}
		puts(tot==n&&sum==tar[0]?"YES":"NO");
	}
	return 0;
}

