	{
		cin >> w[i];
	}
	for (i = 0; i < m; i++)
	{
		cin >> c[i];
	}
	sort(w, w + n);
	sort(c, c + m);
	for (i = 0, j = 0; i < n; i++)
	{
		if (i[w] >= j[c])
		{
			ans++;
			j++;
		}
	}
	cout << ans;
	return 0;
}
