}
void update(int x, int v) {
	for(int i = x; i <= n; i += lowbit(i)) {
		c[i] += v;
	}
}
int query(int x) {
	int res = 0;
	for(int i = x; i >= 1; i -= lowbit(i)) {
		res += c[i];
	}
	return res;
}
