#include<bits/stdc++.h>
#define ls x<<1
#define rs x<<1|1
using namespace std;
struct node{
    int x,y;
}a[100010];
int T,n,pre[100010],ans;
bool cmp1(node A,node B){
    return A.x>B.x;
}
bool cmp2(node A,node B){
    return A.y>B.y;
}
int sum[100010<<3];
vector <int> q[100010];
void bulid(int x,int l,int r){
    if(l==r){
        sum[x]=0;
        return;
    }
    int mid=(l+r)>>1;
    bulid(ls,l,mid);
    bulid(rs,mid+1,r);
    sum[x]=sum[ls]+sum[rs];
}
void update(int x,int l,int r,int st){
    if(l==r&&l==st){
        ++sum[x];
        return;
    }
    int mid=(l+r)>>1;
    if(st<=mid){
        update(ls,l,mid,st);
    }
    else update(rs,mid+1,r,st);

    sum[x]=sum[ls]+sum[rs];
}
bool check(int x,int l,int r,int s){
    if(sum[x]<s){
        return 0;
    }
    if(sum[x]==s){
        return 1;
    }
    if(l==r){
        return 0;
    }
    int mid=(l+r)>>1;
    if(sum[ls]>=s){
        return check(ls,l,mid,s);
    }
    else return check(rs,mid+1,r,s-sum[ls]);
}
void solve(){
    bulid(1,1,n);
    for(int i=1;i<=n;i++){
        q[i].clear();
    }
    for(int i=1;i<=n;i++){
        q[a[i].x].push_back(a[i].y);
    }
    for(int i=n;i;--i){
        for(auto j:q[i]){
            update(1,1,n,j);
        }
        if(check(1,1,n,n>>1)){
            ans=3;
            return;
        }
    }
    bulid(1,1,n);
    for(int i=1;i<=n;i++){
        q[i].clear();
    }
    for(int i=1;i<=n;i++){
        q[a[i].y].push_back(a[i].x);
    }
    for(int i=n;i;--i){
        for(auto j:q[i]){
            update(1,1,n,j);
        }
        if(check(1,1,n,n>>1)){
            ans=3;
            return;
        }
    }
    bulid(1,1,n);
}
signed main(){
    scanf("%d",&T);
    while(T--){
        scanf("%d",&n);
        for(int i=1;i<=n;i++){
            scanf("%d%d",&a[i].x,&a[i].y);
        }
        ans=0;
        for(int i=1;i<=n;i++){
            pre[i]=0;
        }
        for(int i=1;i<=n;i++){
            pre[a[i].x]++;
        }
        for(int i=1;i<=n;i++){
            pre[i]+=pre[i-1];
            if((pre[i]<<1)==n){
                ans=2;
                break;
            }
        }
        for(int i=1;i<=n;i++){
            pre[i]=0;
        }
        for(int i=1;i<=n;i++){
            pre[a[i].y]++;
        }
        for(int i=1;i<=n;i++){
            pre[i]+=pre[i-1];
            if((pre[i]<<1)==n){
                ans=2;
                break;
            }
        }
        if(!ans){
            solve();
        }
        if(!ans){
            ans=4;
        }
        printf("%d\n",ans);
    }
    return 0;
}
