		for(int j=1;j<=m;j++)
			if(f1[i][j]>1&&f2[i][j]>2){
				long long now=f1[i][j]-1;
				ans=(ans+(now*f3[i][j]%mod))%mod;
			}
	printf("%lld ",ans*c%mod);
}
void F(){
	ans=0;
	for(int i=1;i<=n;i++)
		for(int j=1;j<=m;j++)
			if(f1[i][j]>1&&f2[i][j]>3){
				long long now=f1[i][j]-1;
				ans=(ans+(now*f4[i][j]%mod))%mod;
			}
	printf("%lld\n",ans*f%mod);
}
int main(){
	//freopen("plant.in","r",stdin);
