const int N = 1e4+5;
vector<int> v[N];
queue<int> q;
int n, k, p, ind[N], f[N], vv, s, x, r;
bool flag[N];
int main(){
	ios::sync_with_stdio(0);
    cin.tie(0), cout.tie(0);
	memset(f, 0xc0, sizeof(f));
	cin>>n>>k>>p;
	for(int i = 1; i <= p; i++){
		cin>>x;
		q.push(x); //起点
		f[x] = 0;
	}
	cin>>r;
	for(int i = 1; i <= r; i++){
		cin>>vv>>s;
		for(int j = 1; j <= s; j++){
			cin>>x;
			v[x].push_back(vv);
			ind[vv]++;
		} //建边
	}
	while(!q.empty()){
		int d = q.front();
		q.pop();
		for(int i = 0; i < v[d].size(); i++){
			ind[v[d][i]]--;
			if(ind[v[d][i]]==0){
			    if(f[v[d][i]] < 0) f[v[d][i]] = max(f[v[d][i]], f[d] + 1); //注意环，不知道是不是数据水，把判断条件删掉或把这句话放外面都能AC
			    q.push(v[d][i]);
			}
		}
	}
	if(f[k] < 0) cout<<-1<<endl; //-1
	else cout<<f[k]<<endl;
	return 0;
}
