# code
```cpp
#include<bits/stdc++.h>
using namespace std;
int n,m,i,j,ans,r,qq,cnt,a,b,l,h[50005*30];
int k,p,dis[50005],aa[50005],w[50005],flag;//dis[i]是每道题所需的最小天数
queue<int>q;
struct AB{
	int a,b,n;
}d[5000005];//至少开这么大的空间才够
void cun(int a,int b){
	d[++l].a=a,d[l].b=b;
	d[l].n=h[a],h[a]=l;
}
int main(){
	scanf("%d%d%d",&n,&k,&p);
	for(i=1;i<=p;i++){
		scanf("%d",&aa[i]);
		if(aa[i]==k) flag=1;//如果有这个数就标记
	} 
	scanf("%d",&r);
	for(i=1;i<=r;i++){
		scanf("%d%d",&m,&cnt);
		for(j=1;j<=cnt;j++){
			scanf("%d",&qq);
			w[m]++;
			cun(qq,m);
			if(m==k) flag=1;
		}
	}
	if(flag==0){//若此时还没有出现就直接输出
		printf("-1");
		return 0;
	}
	memset(dis,63,sizeof(dis));
	for(i=1;i<=n;i++){
		if(w[i]==0) q.push(i),dis[i]=0; 
	}//将入度为0的题入队
	while(q.size()!=0){
		a=q.front();
		q.pop();
		for(i=h[a];i;i=d[i].n){
			b=d[i].b;
			w[b]--;
			if(w[b]==0){
				q.push(b);
				dis[b]=min(dis[b],dis[a]+1);
			} //入度为0就更新答案
		}
	}
	if(dis[k]==dis[0]) printf("-1");//判断是否有环
	else printf("%d",dis[k]);
	return 0;
}
```
