        {
            b[i] = 600;
            c[i] -= 1000;
        }
        a[i] = round(c[i] / 1000.0 * 400.0);
        if((a[i] / 400.0 * 1000.0) < c[i]) // 判断:取得的c[i]是否与给出的c[i]相等，否则进行微调
        {
            if(b[i] == 600) // 这里要判断边界，下面同理。
            {
