```cpp
#include <bits/stdc++.h>
#define int long long
using namespace std;

const int MAXN=100005; 
int a[MAXN];

void solve(){
	int n,A,B; cin>>n>>A>>B;
	if(A+B>n*(n+1)/2) return cout<<-1<<'\n',void();
	for(int i=1;i<=n;i++) a[i]=i;

	int sum=0,cur=1;
	for(int i=1;i<=n/2;i++) sum+=a[i];
	if(sum<A){
		for(int i=n;i>=1;i--){
			if(sum+a[i]-a[n-i+1]>=A){
				int need=A-sum+a[n-i+1];
//				cout<<"need="<<need<<'\n';
				if(need<=n/2) swap(a[n/2+1-A+sum],a[n/2+1]);
				else swap(a[n-i+1],a[need]);
				break;
			}
			else{
				sum+=a[i]-a[n-i+1];
				swap(a[i],a[n-i+1]);
			}
		}
	}

	int ssum=0; sum=0;
	for(int i=1;i<=n/2;i++) sum+=a[i];
	for(int i=n/2+1;i<=n;i++) ssum+=a[i];
	if(ssum<B || sum<A) return cout<<-1<<'\n',void();
	for(int i=1;i<n;i++) cout<<a[i]<<' ';
	cout<<a[n]<<'\n';
}

signed main(){
	int T; cin>>T;
	while(T--) solve();
	return 0;
}
